package hsbcMock;

public class Allzerosubstr
{
	
	public static void main(String[] args)
	{
		// https://leetcode.cn/problems/number-of-substrings-with-only-1s/description/
		String[] words = { "0110111", "101", "111111", "000", "1111111111011010011",
				"" };
		for (int i = 0; i < words.length; i++)
		{
			System.out.println(numSub(words[i]));
		}
	}
	
	public static int numSub(String word)
	{
		long total = 0;
		int n = word.length();
		long num_consecutive = 0;
		final int MODULE = 1000000007;
		for (int i = 0; i < n; i++)
		{
			char ch = word.charAt(i);
			if (ch == '0')
			{
				total += num_consecutive * (num_consecutive + 1) / 2;
				total %= MODULE;
				num_consecutive = 0;
			}
			else
			{ // ch == '1'
				num_consecutive++;
			}
		}
		total += num_consecutive * (num_consecutive + 1) / 2;
		total %= MODULE;
		return (int) total;
	}
	
}
